import React, { useState, useEffect, useRef } from "react";
import ChartWithSecondaryAxis from "./ChartWithSecondaryAxis";
import html2canvas from "html2canvas";




const ReportForm = () => {
  const [formData, setFormData] = useState({
    patientName: "",
    patientID: "",
    referredBy: "",
    lastMenstrualPeriod: "",
    gravida: "",
    numberOfFetus: "",
    parity: "",
    methodOfConception: "",
    previousTrisomy21: false,
    previousTrisomy18: false,
    previousTrisomy13: false,
    previousTurner: false,
    previousNTD: false,
    dateOfBirth: "",
    diabetes: "",
    weight: "",
    hcgInjection: false,
    hcgInjectionDate: "",
    ethnicity: "",
    sonologistName: "",
    ultrasoundDate: "",
    bpd: "",
    gestationalAgeBiometrics: "",
    gestationalAgeUSG: "",
    fetalHeartRate: "",
    crl: "",
    nt: "",
    dateOfTesting: "",
    immunoassayAnalyzer: "",
    freeBHCG: "",
    momBHCG: "",
    pappa: "",
    momPappa: "",
    ntValue: "",
    momNT: "",
    ageRelatedRisk: "",
