import React, { useState, useRef } from "react";
import ChartWithSecondaryAxis from "../views/ChartWithSecondaryAxis";
import html2canvas from "html2canvas";

const TripleMarker = () => {
  const [formData, setFormData] = useState({
    patientName: "",
    patientID: "",
    referredBy: "",
    lastMenstrualPeriod: "",
    gravida: "",
    numberOfFetus: "",
    parity: "",
    methodOfConception: "",
    previousTrisomy21: false,
    previousTrisomy18: false,
    previousTrisomy13: false,
    previousTurner: false,
    previousNTD: false,
    dateOfBirth: "",
    diabetes: "",
    weight: "",
    hcgInjection: false,
    hcgInjectionDate: "",
    ethnicity: "",
    sonologistName: "",
    ultrasoundDate: "",
    bpd: "",
    gestationalAgeBiometrics: "",
    gestationalAgeUSG: "",
    fetalHeartRate: "",
    crl: "",
    dateOfTesting: "",
    immunoassayAnalyzer: "",
    alphaFetoproteinLevel: "",
    hcgBetaLevel: "",
    uE3Level: "",
    ageRelatedRisk: "",
    trisomy21Risk: "",
    trisomy1318Risk: "",
    finalAssessment: "",
  });

  const chartRef = useRef();

  const [ageRelatedRisk, setAgeRelatedRisk] = useState(0);
  const [t21Risk, setT21Risk] = useState(0);
  const [t1813Risk, setT1813Risk] = useState(0);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: e.target.type === "checkbox" ? e.target.checked : e.target.value,
    }));

    if (name === "ageRelatedRisk") {
      setAgeRelatedRisk(value);
    } else if (name === "trisomy21Risk") {
      setT21Risk(value);
    } else if (name === "trisomy1318Risk") {
      setT1813Risk(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const chartElement = chartRef.current;
    const chartCanvas = await html2canvas(chartElement);
    const chartImageCanvas = chartCanvas.toDataURL("image/png");

    const reportData = {
      "convertTo": "pdf",
      "data": {
        ...formData,
        chartImage: chartImageCanvas,
      },
    };

    fetch(
      "https://render.carbone.io/render/0f53cb4a3a905f01cc9e584a0b4539e86eb2f924409bdb9df7a2b9d1b1f4fcaa",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization":
            "test_eyJhbGciOiJFUzUxMiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiI4Njk1MDg3MzUwMDE4MDM2MDEiLCJhdWQiOiJjYXJib25lIiwiZXhwIjoyMzc1MzY4NzE4LCJkYXRhIjp7InR5cGUiOiJ0ZXN0In19.ARPICgDJzf1ba0Z1xmfbquTy5UU_TPy4jXTNHVCGRF_sswZXufyz1q1iz3wIDqO3kFwPrizgz4dJ75DubpnyCd5bAHNeaf1UFyZpZZLu59aBO463J45dHP_BdXdxK7cTa0u6cUfDaQnLlm8URv3frJPjNb3FJ4pNb2RRRCFaIqcQ9xll",
          "carbone-version": "4",
        },
        body: JSON.stringify(reportData),
      }
    )
      .then((response) => response.json())
      .then((data) => {
        let renderID = data.data.renderId;
        window.open(`https://render.carbone.io/render/${renderID}`, "_blank");
      })
      .catch((error) => {
        console.log("Error:", error);
      });
  };

  return (
    <>
      <div ref={chartRef}>
        <ChartWithSecondaryAxis
          ageRelatedRisk={ageRelatedRisk}
          t21Risk={t21Risk}
          t1813Risk={t1813Risk}
        />
      </div>
      <div id="report-form">
        <form onSubmit={handleSubmit}>
          {/* ... (other form fields remain the same) ... */}

          <label>
            Level of Alpha-fetoprotein:
            <input
              type="text"
              name="alphaFetoproteinLevel"
              value={formData.alphaFetoproteinLevel}
              onChange={handleChange}
            />
          </label>

          <label>
            Level of hCG + beta:
            <input
              type="text"
              name="hcgBetaLevel"
              value={formData.hcgBetaLevel}
              onChange={handleChange}
            />
          </label>

          <label>
            Level of uE3:
            <input
              type="text"
              name="uE3Level"
              value={formData.uE3Level}
              onChange={handleChange}
            />
          </label>

          {/* ... (remaining form fields and submit button) ... */}
        </form>
      </div>
    </>
  );
};

export default TripleMarker;
